import { flatStanley } from './theme';

export const getStyle = (key) => flatStanley?.key;

export const getProp = (key, callback) => (p) => {
  const val = p?.key;
  return callback ? callback(val) : val;
};

export const getBool = (key, ifTrue, ifFalse) => (p) =>
  p?.key ? ifTrue || '' : ifFalse || '';
